import React, { Component } from 'react'
import IssueService from '../services/IssueService'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'react-bootstrap-table-next/dist/react-bootstrap-table2.css';
import 'react-bootstrap-table2-paginator/dist/react-bootstrap-table2-paginator.min.css';
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';

class ListAllIssues extends Component {
    constructor(props) {
        super(props)

        this.state = {
                issues : []
        }
        
        
     this.editIssue = this.editIssue.bind(this);
    
    }
    
    editIssue(issue){
        this.props.history.push({
            pathname:`/issues/updateissue/${issue.issueId}`,
            state:{
                issue:issue
            }
        });
    }
    viewIssue(issueId){
        this.props.history.push(`/issues/${issueId}`);
    }
    

    componentDidMount(){
        IssueService.getIssues().then((res) => {
            this.setState({ issues : res.data});
        });
    }
   
    
    render() {
       /* const value=this.state.issues.map(issue=>issue.userId)
       if(!value){
           return null
       } */
       const pagination = paginationFactory({
        page: 2,
        sizePerPage: 5,
        lastPageText: '>>',
        firstPageText: '<<',
        nextPageText: '>',
        prePageText: '<',
        showTotal: true,
        alwaysShowAllBtns: true,
        onPageChange: function (page, sizePerPage) {
          console.log('page', page);
          console.log('sizePerPage', sizePerPage);
        },
        onSizePerPageChange: function (page, sizePerPage) {
          console.log('page', page);
          console.log('sizePerPage', sizePerPage);
        }
      });
        return (
            <div>
                 <div ><h2 className="text-center" >Issue List</h2></div>
                 
                 <div className = "row">
                        <BootstrapTable bootstrap4   className = "table table-striped table-bordered" pagination={pagination}/>

                            <thead>
                                <tr>
                                    <th > Issue Id</th>
                                    <th > Issue Description</th>
                                    <th > Issue Status</th>
                                   {/*  <th > Customer Id</th>  */}
                                    <th > Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.issues.map(
                                        issue => 
                                        <tr key = {issue.issueId}>
                                             <td align="center"> { issue.issueId} </td>
                                             <td> {issue.issueDescription}</td>   
                                             <td> { issue.issueStatus}</td>
                                            {/*  <td align="center"> { issue.userId.userId}</td>   */}
                                             <td>
                                                 <button onClick={ () => this.editIssue(issue)} className="btn btn-info">Update </button>
                                                 
                                                 <button style={{marginLeft: "10px"}} onClick={ () => this.viewIssue(issue.issueId)} className="btn btn-info">View </button> 
                                             </td> 
                                             
                                        </tr>
                                    )
                                }
                            </tbody>
                        

                 </div>

            </div>
        )
    }
}

export default ListAllIssues
