import React, { Component } from 'react'
import IssueService from '../services/IssueService'

class ListAllIssues extends Component {
    constructor(props) {
        super(props)

        this.state = {
                issues : []
        }
        this.editIssue = this.editIssue.bind(this);
        
    }
   /*  editIssue(){
        this.props.history.push(`/issues/updateissue`);
    }
    viewIssue(issueId){
        this.props.history.push(`/issues/${issueId}`);
    } */
    

    componentDidMount(){
        IssueService.getIssues().then((res) => {
            this.setState({ issues : res.data});
        });
    }

    render() {
        return (
            <div>
                 <h2 className="text-center" >Issue List</h2>
                 
                 <div className = "row">
                        <table  className = "table table-striped table-bordered">

                            <thead>
                                <tr>
                                    <th > Issue Id</th>
                                    <th > Issue Description</th>
                                    <th > Issue Status</th>
                                    <th > Customer Id</th>
                                    <th > Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.issues.map(
                                        issue => 
                                        <tr key = {issue.issueId}>
                                             <td align="center"> { issue.issueId} </td>
                                             <td> {issue.issueDescription}</td>   
                                             <td> { issue.issueStatus}</td>
                                             <td align="center"> { issue.userId.userId}</td>
                                             {/* <td>
                                                 <button onClick={ () => this.editIssue()} className="btn btn-info">Update </button>
                                                 
                                                 <button style={{marginLeft: "10px"}} onClick={ () => this.viewEmployee(issue.issueId)} className="btn btn-info">View </button> 
                                             </td> */}
                                             
                                        </tr>
                                    )
                                }
                            </tbody>
                        </table>

                 </div>

            </div>
        )
    }
}

export default ListAllIssues
