import React, { Component } from 'react'
import IssueService from '../services/IssueService'
import {Alert} from 'react-alert'

class UpdateIssues extends Component {
    constructor(props) {
        super(props)

        this.state = {           
            /*  issueId: this.props.match.params.issue.,
             issueDescription:'',
            issueStatus:'',
            userId:'' */
            issueobj: this.props.match.issue
        

            
        }
        
        this.updateIssue = this.updateIssue.bind(this);
    }
    componentDidMount(){
        console.log(this.state.issueobj)
        /*  IssueService.getIssueById(this.state.issueobj.issueId).then( (res) =>{
            let issue = res.data;
            this.setState({issueId: issue.issueId,
                issueDescription: issue.issueDescription,
                issueStatus : issue.issueStatus,
                userId: issue.userId.userId
            });
        }); */
    }


    updateIssue = (e) => {
      
        e.preventDefault();
        let issue={issueId: this.state.issueId,
            issueDescription: this.state.issueDescription,
            issueStatus : this.state.issueStatus,
            userId: this.state.userId}

        console.log('issue => ' + JSON.stringify(issue));
        
        IssueService.updateIssue(issue).then( res => {
            this.props.history.push('/issues/allissue');
           
           
            
            
        });
    }
    
    cancel(){
        this.props.history.push('/issues/allissue');
    }
   

    render() {
        
        return (
            <div>
                <br></br>
                   <div className = "container">
                        <div className = "row">
                            <div className = "card col-md-6 offset-md-3 offset-md-3">
                                <h3 className="text-center">Update Issue</h3>
                                <div className = "card-body">
                                    
                                        <button className="btn btn-success" onClick={this.updateIssue}>Resolve</button>
                                        <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Cancel</button>
                                    
                                </div>
                            </div>
                        </div>

                   </div>
            </div>
        )
    }
}

export default UpdateIssues
