import React, { Component } from 'react'
import IssueService from '../services/IssueService'

class ViewIssueById extends Component {
    constructor(props) {
        super(props)

        this.state = {
            issueId: this.props.match.params.issueId,
            issue: {}
        }
    }

    componentDidMount(){
        IssueService.getIssueById(this.state.issueId).then( res => {
            this.setState({issue: res.data});
        })
    }

    render() {
        return (
            <div>
                <br></br>
                <div className = "card col-md-6 offset-md-3">
                    <h3 className = "text-center"> View Issue Details</h3>
                    <div className = "card-body">
                        <div className = "row">
                            <label> Issue Id: </label>
                            <div> { this.state.issueId}</div>
                        </div>
                        <div className = "row">
                            <label> IssueDescription </label>
                            <div> { this.state.issue.issueDescription}</div>
                        </div>
                        <div className = "row">
                            <label> Issue Status: </label>
                            <div> { this.state.issue.issueStatus}</div>
                        </div>
                        <div className = "row">
                            <label> Customer Id: </label>
                            { this.state.issue.map(i=>i.userId.name)}
                        </div>
                    </div>

                </div>
            </div>
        )
    }
}

export default ViewIssueById
